-- Enable the pgvector extension
CREATE EXTENSION IF NOT EXISTS vector;

-- ============================================
-- Table: tax_documents
-- Stores German tax law paragraphs with embeddings for RAG
-- ============================================
CREATE TABLE IF NOT EXISTS public.tax_documents (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  citation TEXT NOT NULL,           -- e.g., "§ 1 AO"
  title TEXT NOT NULL,              -- e.g., "Anwendungsbereich"
  content TEXT NOT NULL,            -- Full text of the paragraph
  law_type TEXT NOT NULL,           -- 'AO', 'UStG', 'EStG', etc.
  embedding vector(384),            -- gte-small embedding (384 dimensions)
  created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Add comments for documentation
COMMENT ON TABLE public.tax_documents IS 'German tax law documents with vector embeddings for semantic RAG search';
COMMENT ON COLUMN public.tax_documents.citation IS 'Legal citation reference (e.g., § 42 AO)';
COMMENT ON COLUMN public.tax_documents.law_type IS 'Type of tax law: AO, UStG, EStG, GewStG, KStG';
COMMENT ON COLUMN public.tax_documents.embedding IS 'Vector embedding from Supabase/gte-small model (384 dims)';

-- Create index for fast similarity search
-- Note: IVFFlat requires at least 100 rows for lists=100, using HNSW for small datasets
CREATE INDEX IF NOT EXISTS idx_tax_documents_embedding 
  ON public.tax_documents 
  USING hnsw (embedding vector_cosine_ops);

-- Create index for law_type filtering
CREATE INDEX IF NOT EXISTS idx_tax_documents_law_type 
  ON public.tax_documents(law_type);

-- ============================================
-- Function: match_tax_documents
-- Performs semantic similarity search on tax documents
-- ============================================
CREATE OR REPLACE FUNCTION public.match_tax_documents(
  query_embedding vector(384),
  match_threshold FLOAT DEFAULT 0.5,
  match_count INT DEFAULT 3
)
RETURNS TABLE (
  id UUID,
  citation TEXT,
  title TEXT,
  content TEXT,
  law_type TEXT,
  similarity FLOAT
)
LANGUAGE sql STABLE
AS $$
  SELECT
    tax_documents.id,
    tax_documents.citation,
    tax_documents.title,
    tax_documents.content,
    tax_documents.law_type,
    1 - (tax_documents.embedding <=> query_embedding) AS similarity
  FROM public.tax_documents
  WHERE 1 - (tax_documents.embedding <=> query_embedding) > match_threshold
  ORDER BY tax_documents.embedding <=> query_embedding
  LIMIT match_count;
$$;

COMMENT ON FUNCTION public.match_tax_documents IS 'Semantic search for tax documents using cosine similarity';

-- ============================================
-- RLS Policies (tax documents are public read)
-- ============================================
ALTER TABLE public.tax_documents ENABLE ROW LEVEL SECURITY;

-- Allow all authenticated users to read tax documents
CREATE POLICY "Tax documents are readable by authenticated users"
  ON public.tax_documents
  FOR SELECT
  TO authenticated
  USING (true);

-- Allow service role to insert/update (for seeding)
CREATE POLICY "Service role can manage tax documents"
  ON public.tax_documents
  FOR ALL
  TO service_role
  USING (true)
  WITH CHECK (true);

