INSERT INTO public.tax_documents (citation, title, content, law_type, embedding) VALUES ('§ 146a AO', 'Ordnungsvorschriften für die Buchführung und für Aufzeichnungen mittels elektronischer Aufzeichnungssysteme', 'Wer aufzeichnungspflichtige Geschäftsvorfälle oder andere Vorgänge mit Hilfe eines elektronischen Aufzeichnungssystems erfasst, hat ein elektronisches Aufzeichnungssystem zu verwenden, das jeden aufzeichnungspflichtigen Geschäftsvorfall einzeln, vollständig, richtig, zeitgerecht und geordnet aufzeichnet. Das elektronische Aufzeichnungssystem muss mit einer zertifizierten technischen Sicherheitseinrichtung ausgestattet sein (TSE - Technische Sicherheitseinrichtung). Die Anforderungen gelten insbesondere für elektronische Registrierkassen.', 'AO', '[-0.021298067644238472,0.02639772929251194,0.04184923321008682,0.007222697138786316,-0.006610268261283636,-0.006408259738236666,0.03529954329133034,0.048665206879377365,0.0029167290776968002,-0.02082844451069832,-0.0014998898841440678,-0.0706367939710617,0.039546266198158264,0.023850092664361,-0.0022515137679874897,0.019079264253377914,-0.020354334264993668,0.04216460511088371,-0.0013362285681068897,0.017444966360926628,0.056310731917619705,-0.059042513370513916,0.01692473515868187,-0.036101121455430984,0.06497013568878174,0.0922279879450798,0.009949157014489174,-0.028313124552369118,-0.025238730013370514,-0.22562357783317566,-0.018769336864352226,-0.07516494393348694,0.05573675036430359,0.010161657817661762,-0.023968970403075218,-0.06704839318990707,-0.013287420384585857,0.026657531037926674,-0.02573421224951744,0.008038503117859364,-0.0064338622614741325,-0.003109768033027649,0.0058424342423677444,-0.07596591860055923,-0.04587073624134064,-0.05772392451763153,-0.004999194759875536,-0.04683663323521614,-0.02147183194756508,-0.026267433539032936,0.02694815956056118,-0.022499222308397293,0.03192552179098129,0.0001129041047533974,0.002486912999302149,0.02415408194065094,0.05032029747962952,0.04685592278838158,0.0656