INSERT INTO public.tax_documents (citation, title, content, law_type, embedding) VALUES ('§ 162 AO', 'Schätzung von Besteuerungsgrundlagen', 'Soweit die Finanzbehörde die Besteuerungsgrundlagen nicht ermitteln oder berechnen kann, hat sie diese zu schätzen. Dabei sind alle Umstände zu berücksichtigen, die für die Schätzung von Bedeutung sind. Geschätzt werden kann insbesondere dann, wenn der Steuerpflichtige über seine Angaben keine ausreichenden Aufklärungen zu geben vermag oder weitere Auskunft oder eine Versicherung an Eides statt verweigert oder seine Mitwirkungspflicht nach § 90 Abs. 2 verletzt.', 'AO', '[-0.00013529277930501848,0.026359129697084427,0.026131460443139076,-0.010562252253293991,0.043121665716171265,0.02843681536614895,0.011497298255562782,0.012673444114625454,-0.01625899225473404,-0.03078191913664341,0.025699671357870102,-0.09600571542978287,0.025339167565107346,0.035616930574178696,-0.008988938294351101,0.012990240007638931,0.011143224313855171,0.05775604397058487,-0.049100276082754135,0.06622956693172455,0.03681947663426399,-0.05578344687819481,-0.00039953747182153165,-0.06291057914495468,0.05444789305329323,0.04207415133714676,-0.02719985321164131,-0.05252252146601677,-0.014725517481565475,-0.16780278086662292,-0.002859006868675351,-0.08058735728263855,-0.02409103326499462,0.006259322166442871,0.003110971301794052,-0.03771943598985672,-0.04111470654606819,0.036276768893003464,-0.023685051128268242,0.05209381505846977,0.008686920627951622,0.08661302924156189,0.004188959952443838,-0.0672260969877243,-0.021712884306907654,-0.027255022898316383,0.01628265343606472,-0.010664631612598896,-0.04029431939125061,-0.0006652561132796109,0.02853650227189064,-0.03972545638680458,0.013767162337899208,-0.012656397186219692,-0.02245132252573967,0.039762407541275024,0.04951247200369835,0.020321814343333244,0.003215186996385455,0.053762324154376984,-0.0005295026930980384,0.011680916883051395,-0.2591121196746826,0.03791293874382973,0.042692363262176514,0.024736519